import React from 'react';
import { View, SafeAreaView, ImageBackground, TouchableOpacity } from 'react-native';
import { Button, Card, TextInput } from 'react-native-paper';
import { loginStyle } from './loginStyle';

export default class LoginScreen extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
            login: "",
            mdp: ""

        }
    }
    validate_field = () => {
        const { login, mdp } = this.state
        if (login = "") {
            console.log("Please fill login")
            return false
        } else if (mdp = "") {
            console.log("Please fill password")
            return false
        }
        return true
    }

    making_api_call = () => {
        if (this.validate_field()) {
            this.props.navigation.navigate("roleAdmin")

        }
    }
    render() {

        return (
            <ImageBackground
                source={require('../images/backgroundAccueil.jpg')}
                style={{ width: "100%", height: "100%" }}
            >

                <SafeAreaView style={loginStyle.content}>


                    <View style={loginStyle.View}>

                        <Card >
                            <Card.Content>
                                <Card.Title title="Welcome to Learn EZ" titleStyle={loginStyle.cardTitle}></Card.Title>
                                <TextInput label="Email" keyboardType='"email-address' onChangeText={(value) => this.useState({ login: value })} ></TextInput>
                                <TextInput label="Password" secureTextEntry={true} onChangeText={(value) => this.useState({ mdp: value })}></TextInput>
                                <Button uppercase={false}>Forget email/password</Button>
                                <Button mode="contained" onPress={() => this.making_api_call()}>Login</Button>
                                <Button onPress={() => this.props.navigation.navigate("register")}>Register</Button>
                            </Card.Content>

                        </Card>
                    </View>
                </SafeAreaView>
            </ImageBackground >

        )
    }
}
