import React from "react";
import { SafeAreaView, ScrollView, View, ImageBackground } from "react-native";
import { Appbar, Button, TextInput } from "react-native-paper";
import { addMatiereStyle } from "./addMatiereStyle";


export default class PersonList2 extends React.Component {
    state = {
        id: '',
        login: '',
        mdp: '',
    }




    handleChangeid = event => {
        this.setState({ id: event.target.value });
    }
    handleChangelogin = event => {
        this.setState({ login: event.target.value });
    }
    handleChangemdp = event => {
        this.setState({ mdp: event.target.value });
    }

    handleSubmit = event => {
        event.preventDefault();

        const etudiant = {
            id: this.state.id,
            login: this.state.login,
            mdp: this.state.mdp
        };

        axios.post(`http://localhost:5700/api/v1/etudiant`, etudiant)
            .then(res => {
                console.log(etudiant)
                console.log(res);
                console.log(res.data);

            })
    }

    render() {

        return (
            <ImageBackground

                source={require('../images/backgroundAccueil.jpg')}
                style={{
                    width: "100%", height: "100%"
                }}>
                <SafeAreaView>
                    <ScrollView >
                        <Appbar>
                            <Appbar.BackAction onPress={() => this.props.navigation.navigate("managingMatieres")} />
                        </Appbar>
                        <View style={addMatiereStyle.content}>
                            <TextInput label="Id" />

                            <TextInput label="Name of module" />


                            <Button mode="contained" style={addMatiereStyle.button}>Add</Button>
                        </View>

                    </ScrollView>
                </SafeAreaView>
            </ImageBackground>

        )
    }
}
