import React from 'react';
import { View, SafeAreaView, ImageBackground, TouchableOpacity } from 'react-native';
import { Button, Card, TextInput } from 'react-native-paper';
import { loginStyle } from './loginStyle';

import { useRef, useState, useEffect } from 'react';
const LoginScreen = () => {


    const userRef = useRef();
    const errRef = useRef();

    const [login, setUser] = useState('');
    const [mdp, setmdp] = useState('');
    const [errMsg, setErrMsg] = useState('');
    const [success, setSuccess] = useState(false);

    useEffect(() => {
        userRef.current.focus();
    }, [])

    useEffect(() => {
        setErrMsg('');
    }, [login, mdp])


    return (
        <ImageBackground
            source={require('../images/backgroundAccueil.jpg')}
            style={{ width: "100%", height: "100%" }}
        ><SafeAreaView style={loginStyle.content}>


                <View style={loginStyle.View}>

                    <Card >
                        <Card.Content>
                            <Card.Title title="Welcome to Learn EZ" titleStyle={loginStyle.cardTitle}></Card.Title>
                            <Text ref={errRef} className={errMsg ? "errmsg" : "offscreen"} aria-live="assertive">{errMsg}</Text>
                            <TextInput label="Login" id="login" ref={useRef} autoComplete="off" onchange={(e) => setUser(e.target.value)} value={login} required keyboardType='"email-address' ></TextInput>
                            <TextInput label="Password" id="mdp" onchange={(e) => setmdp(e.target.value)} value={mdp} required secureTextEntry={true} ></TextInput>
                            <Button uppercase={false}>Forget email/password</Button>
                            <Button mode="contained" onPress={() => this.props.navigation.navigate("roleAdmin")}>Login</Button>
                            <Button onPress={() => this.props.navigation.navigate("register")}>Register</Button>
                        </Card.Content>

                    </Card>
                </View>
            </SafeAreaView>
        </ImageBackground >

    )

}
export default LoginScreen
