import React from 'react'
import { View, Text, ImageBackground, TouchableOpacity, Image } from 'react-native'
import { ScrollView, TextInput } from 'react-native-gesture-handler'
import CourseList from '../screens/CourseList'

export default class Home extends React.Component {
    render() {
        return (
            <ImageBackground
                source={require('../images/background.png')}
                style={{ width: "100%", height: "100%" }}
            >
                <ScrollView>
                    <View style={{
                        width: "100%",
                        alignItems: "flex-end",
                        paddingHorizontal: 20
                    }}>
                        <View style={{
                            paddingHorizontal: 10,
                            paddingVertical: 12,
                            borderRadius: 10,
                            marginTop: 30,
                            backgroundColor: "#d1a0a7"
                        }}>
                            <Image
                                onPress={() => this.props.navigation.navigate("MyComponent")}
                                source={require('../images/hum.png')}
                                style={{ height: 15, width: 20 }}
                            />
                        </View>
                    </View>
                    <Text style={{
                        paddingHorizontal: 20,
                        fontSize: 35,
                        paddingTop: 40,
                        fontFamily: "Bold",
                        color: "#FFF"
                    }}>
                        Welcome back Louay
                    </Text>

                    <View style={{
                        flexDirection: "row",
                        alignItems: "center",
                        backgroundColor: "#FFF",
                        padding: 10,
                        borderRadius: 12,
                        marginHorizontal: 20,
                        marginTop: 20
                    }}>
                        <TextInput
                            placeholder="Search for new knowledge!"
                            placeholderTextColor="#345c74"
                            style={{
                                fontFamily: "Bold",
                                fontSize: 12,
                                width: 280,
                                height: 25,
                                paddingHorizontal: 12
                            }}
                        />
                        <Image
                            source={require('../images/sear.png')}
                            style={{ height: 17, width: 17 }}
                        />
                    </View>
                    <View style={{
                        flexDirection: "row",
                        backgroundColor: "#FFF2F2",
                        marginTop: 15,
                        marginHorizontal: 20,
                        borderRadius: 20,
                        paddingVertical: 30,
                        paddingLeft: 30
                    }}>
                        <View>
                            <Text style={{
                                color: "#345c74",
                                fontSize: 20,
                                fontFamily: "Bold",
                                width: 250,
                                paddingRight: 100
                            }}>
                                Start your future Here !!
                            </Text>
                            <TouchableOpacity
                                onPress={() => this.props.navigation.navigate('Cources')}
                                style={{
                                    flexDirection: "row",
                                    backgroundColor: "#f58084",
                                    alignItems: "center",
                                    marginTop: 20,
                                    width: 150,
                                    paddingVertical: 10,
                                    borderRadius: 14,
                                    paddingHorizontal: 10
                                }}
                            >
                                <Text style={{
                                    color: "#FFF",
                                    fontFamily: "Bold",
                                    fontSize: 12
                                }}>Categories</Text>
                                <Image
                                    source={require('../images/a3.png')}
                                    style={{ marginLeft: 20, width: 8, height: 8 }}
                                />
                            </TouchableOpacity>
                        </View>
                        <Image
                            source={require('../images/undraw.png')}
                            style={{ marginLeft: -80, marginTop: 35 }}
                        />

                    </View>
                    <Text style={{
                        color: "#345c74",
                        fontFamily: "Bold",
                        fontSize: 20,
                        paddingHorizontal: 20,
                        marginTop: 20,
                        marginBottom: 10
                    }}>Courses in progress</Text>

                    <CourseList
                        onPress={() => this.props.navigation.navigate("C")}
                        img={require('../images/c.png')}
                        title="Language C"
                        bg="#276DC2"
                    />
                    <CourseList
                        onPress={() => this.props.navigation.navigate("cpp")}
                        img={require('../images/c++.png')}
                        title="language C ++"
                        bg="#78B6FF"
                    />
                    <CourseList
                        onPress={() => this.props.navigation.navigate("AE")}
                        img={require('../images/ae.png')}
                        title="UI Motion Design in After Effects"
                        bg="#fcf2ff"
                    />
                </ScrollView>
            </ImageBackground>
        )
    }
}
